const TEXT_TO_APPEND = "'";

const determineContentType = (input) => {
  if (isJSON(input)) {
    return "JSON";
  }

  if (isQuery(input)) {
    return "QUERY";
  }

  return "UNKNOWN";
};

const isJSON = (input) => {
  try {
    JSON.parse(input);
    return true;
  } catch (e) {
    return false;
  }
};

const convertToObject = (contentType, input) => {
  if (contentType == "QUERY") {
    const queryParts = input.split("&");
    const result = {};
    queryParts.forEach((part) => {
      const [key, value] = part.split("=");
      result[key] = value;
    });
    return result;
  } else if (contentType == "JSON") {
    return JSON.parse(input);
  }
};

const isQuery = (str) => {
  const queryStringRegex = /^[^?]+\=[^?]+(&[^?]+\=[^?]+)*$/;
  return queryStringRegex.test(str);
};

const modifyObject = (object, appendText) => {
  for (let key in object) {
    if (typeof object[key] === "object") {
      object[key] = modifyObject(object[key], appendText);
    } else {
      object[key] = object[key] + appendText;
    }
  }
  return object;
};

// convert back to contentType
const result = (object, contentType) => {
  if (contentType == "QUERY") {
    return Object.entries(object)
      .map(([key, value]) => `${key}=${value}`)
      .join("&");
  } else if (contentType == "JSON") {
    return JSON.stringify(object);
  }
};

export function run(input, sdk) {
  const testInput = sdk.asString(input);
  const contentType = determineContentType(testInput);
  if (contentType === "UNKNOWN") {
    return input;
  }

  const keyValue = convertToObject(contentType, testInput);
  const modifiedObject = modifyObject(keyValue, TEXT_TO_APPEND);
  const resultString = result(modifiedObject, contentType);
  return resultString;
}
